package ch3.sec4;
/** 
 * @author 作者 Kevin Zhou 
 * @version 创建时间：2020年4月23日 上午10:33:57 
 * 类说明 : 6种关系运算符分别为：大于、小于、
 * 大于等于、小于等于、等于和不等于。
 * 关系运算符通常与if和switch等条件和循环语句
 * 一起作用。关系运算的结果为true或者false.
 */
public class OperatorDemo04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(" 3 > 1 = " + (3 > 1));
		System.out.println(" 3 < 1 = " + (3 < 1));
		System.out.println(" 3 >= 1 = " + (3 >= 1));
		System.out.println(" 3 < 1 = " + (3 < 1));
		System.out.println(" 3 <= 1 = " + (3 <= 1));
		System.out.println(" 3 == 1 = " + (3 == 1));
		System.out.println(" 3 != 1 = " + (3 != 1));

	}

}

/*
3 < 1 = false
3 >= 1 = true
3 < 1 = false
3 <= 1 = false
3 == 1 = false
3 != 1 = true
*/